using Microsoft.Extensions.Caching.Memory;namespace ProductManager.Api.Helpers;public static class CacheHelper{    public static MemoryCacheEntryOptions GetDefaultCacheOptions()    {        return new MemoryCacheEntryOptions        {            SlidingExpiration = TimeSpan.FromMinutes(5),            AbsoluteExpiration = DateTimeOffset.Now.AddMinutes(30)        };    }}