using Ardalis.Result;using FluentValidation;using Microsoft.Extensions.Logging;using ProductManager.Api.Models.DTOs;namespace ProductManager.Api.Helpers;public static class ValidationHelper{    public static async Task<Result> ValidateProductInput(        IValidator<ProductInputDTO> validator,         ProductInputDTO inputDto,        ILogger logger)    {        var validationResult = await validator.ValidateAsync(inputDto);        if (!validationResult.IsValid)        {            logger.LogWarning("Product validation failed: {ValidationErrors}",                 string.Join(", ", validationResult.Errors.Select(e => e.ErrorMessage)));                            var errors = validationResult.Errors                .Select(e => new ValidationError(                    e.PropertyName,                    e.ErrorMessage,                    e.ErrorCode,                    ValidationSeverity.Error))                .ToList();                    return Result.Invalid(errors);        }                return Result.Success();    }}