using AutoMapper;using ProductManager.Api.Models.DTOs;using ProductManager.Api.Models.Entity;namespace ProductManager.Api.Mappings;public class MappingProfile : Profile{    public MappingProfile()    {                CreateMap<Product, ProductDTO>();              CreateMap<ProductInputDTO, Product>()            .ForMember(dest => dest.Id, opt => opt.Ignore()); // Ignore Id when mapping from input    }}