using FluentValidation;using ProductManager.Api.Models.DTOs;namespace ProductManager.Api.Validator;public class ProductInputValidator : AbstractValidator<ProductInputDTO>{    public ProductInputValidator()    {        RuleFor(p => p.Name)            .NotEmpty().WithMessage("Product name is required")            .MaximumLength(100).WithMessage("Product name cannot exceed 100 characters");        RuleFor(p => p.Price)            .GreaterThan(0).WithMessage("Price must be greater than zero")            .LessThan(1000000).WithMessage("Price cannot exceed 1,000,000");        RuleFor(p => p.Category)            .NotEmpty().WithMessage("Category is required")            .MaximumLength(50).WithMessage("Category cannot exceed 50 characters");    }}